import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import axios from 'axios';
import { HOST } from '../env';


const initialState = {
    importantQuestion: { data: [], index: 0, style: [], loading: false, error: '', },
    ruleQuestion: { data: [], index: 0, style: [], loading: false, error: '', }
}
export const fetchIQuestionData = createAsyncThunk('question/fetchQuestionData', async () => {
    const response = await axios.get(`${HOST}/Question/get/type/A1`);
    return response.data;
});

export const fetchRuestionData = createAsyncThunk('question/fetchQuestionData', async () => {
    const response = await axios.get(`${HOST}/Question/get/type/A1`);
    return response.data;
});
const handleAsyncThunk = (builder, asyncThunk, targetState) => {
    builder
        .addCase(asyncThunk.pending, targetState => {
            targetState.loading = true;
        })
        .addCase(asyncThunk.fulfilled, (targetState, action) => {
            targetState.loading = false;
            targetState.data = action.payload;
        })
        .addCase(asyncThunk.rejected, (targetState, action) => {
            targetState.loading = false;
            targetState.error = action.error.message;
        });
};

const Slice = createSlice({
    name: 'question',
    initialState,
    reducers: {
        setUser: (state, action) => {
            state.question = action.payload;
        },
        clearUser: state => {
            state.question = null;
        },
    },
    extraReducers: builder => {
        handleAsyncThunk(builder, fetchIQuestionData, state.importantQuestion);
        handleAsyncThunk(builder, fetchRuestionData, state.ruleQuestion);
    }
});



export const { setUser, clearUser } = Slice.actions;

export default Slice.reducer;
