import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import axios from 'axios';
import { HOST } from '../env';

const initialState = {
    importantQuestion: { data: [], index: 0, style: [], loading: false, error: '', },
    ruleQuestion: { data: [], index: 0, style: [], loading: false, error: '', }
}

const Slice = createSlice({
    name: 'question',
    initialState,
    reducers: {
        setUser: (state, action) => {
            state.question = action.payload;
        },
        clearUser: state => {
            state.question = null;
        },
    },
    extraReducers: builder => {
        builder
            .addCase(fetchQuestionData.pending, state => {
                state.status = 'loading';
            })
            .addCase(fetchQuestionData.fulfilled, (state, action) => {
                state.status = 'succeeded';
                state.importantQuestion.data = action.payload;
            })
            .addCase(fetchQuestionData.rejected, (state, action) => {
                state.status = 'failed';
                state.error = action.error.message;
            });
    }
});

export const fetchQuestionData = createAsyncThunk('question/fetchQuestionData', async () => {
    const response = await axios.get(`${HOST}/Question/get/`);
    return response.data;
});

export const { setUser, clearUser } = Slice.actions;

export default Slice.reducer;
