import { Avatar, Text, Surface, DarkTheme, PaperProvider } from 'react-native-paper';
import { SafeAreaProvider } from 'react-native-safe-area-context';
import { StyleSheet, View, ScrollView, TouchableOpacity, Image } from 'react-native';
import React, { useState } from 'react';



export default Main_App = ({ navigation }) => {

  const ButtonCustom = ({ navigate, title, subtitle, image }) => {
    const imageName = image
    const images = {
      rawSearch: require('../assets/rawSearch.png'),
      learn: require('../assets/learn.png'),
      tip: require('../assets/tip.png'),
      question: require('../assets/question.png'),
      bienbao: require('../assets/bienbao.png'),
      test: require('../assets/test.png'),
      default: require('../assets/default.png'),
    };
    return (
      <View style={{ width: '100%', height: '35%' }} >
        <TouchableOpacity onPress={() => navigation.navigate(navigate)} style={{ ...style.ButtonBody, borderRadius: 10, }}>
          <View style={{ flex: 1.5, justifyContent: 'center', alignItems: 'center' }}>
            <Image source={images[imageName]} resizeMode='contain' style={{ flex: 1, }} />
          </View>
          <View style={{ flex: 2, }}>
            <Text style={{ fontSize: 20, fontWeight: 'bold', textAlign: 'center' }}>{title}</Text>
            <Text style={{ fontSize: 10, color: 'grey', textAlign: 'center' }}>{subtitle}</Text>
          </View>
        </TouchableOpacity>
      </View>

    )
  }
  return (
    <SafeAreaProvider style={{ ...style.container, marginBottom: 80, backgroundColor: "#f5f5f5" }}>
      <ScrollView>
        {/* 
        <View style={style.ViewCardUser}>
          <Surface style={style.surfaceUser} theme={DarkTheme}>
            <View style={{ flex: 1 }}>

            </View>
            <View style={style.ViewPercent}>
              <Text style={{ fontSize: 20, color: "red" }}>Bạn cần nỗ lực hơn nữa</Text>
              <Text style={{ fontSize: 15 }}>Tỉ lệ đỗ của bạn: <Text style={{ fontSize: 20, color: "red" }}>0%</Text></Text>
            </View>
          </Surface>
        </View>

        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center', borderRadius: 20, backgroundColor: 'red', }}>
          <Image source={require("../assets/rawSearch.png")} />
        </View>

        <View style={{ flexDirection: 'row', flex: 1, paddingBottom: 50, backgroundColor: 'pink', }}>
          <View style={{ flex: 1, }}>
            <ButtonCustom title="THI THỬ SÁT HẠCH" subtitle="20 đề thi thử, 200 câu hỏi" navigate="RawSearch" image="test" />
            <ButtonCustom title="BIỂN BÁO ĐƯỜNG BỘ" subtitle="Đây đủ biển báo giao thông" navigate="RawSearch" image="bienbao" />
            <ButtonCustom title="TRA CỨU LUẬT" subtitle="Tra cứu nhanh luật an toàn giao thông" navigate="RawSearch" image="rawSearch" />
          </View>
          <View style={{ flex: 1, }}>
            <ButtonCustom title="HỌC LÝ THUYẾT" subtitle="7 chủ đề, 200 câu hỏi" navigate="RawSearch" image="learn" />
            <ButtonCustom title="MẸO THI KẾT QUẢ CAO" subtitle="Mẹo trả lời phân theo các câu hỏi" navigate="RawSearch" image="tip" />
            <ButtonCustom title="CÁC CÂU HAY SAI" subtitle="Lưu lại các câu bạn trả lời sai" navigate="RawSearch" image="question" />
          </View>
        </View> */}



      </ScrollView>
    </SafeAreaProvider >
  )
};

const style = StyleSheet.create({
  container:
  {
    flex: 1,
    flexDirection: 'column'
  },
  surface: {
    margin: 25,
    padding: 10,
    height: 80,
    width: 150,
    elevation: 6,
    borderRadius: 4,
    backgroundColor: "white"
  },
  surfaceUser: {
    flexDirection: 'row',
    padding: '3%',
    margin: '5%',
    elevation: 6,
    borderRadius: 15,
    backgroundColor: "white"
  },
  surfaceText: {
    color: 'black',
  },
  ItemLesson: {
    flex: 2
  },
  ViewCardUser: {
    backgroundColor: 'blue',
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',

  },
  ViewPercent:
  {
    flex: 5,
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center'
  },
  CardUser: {
    elevation: 0,
    borderRadius: 15,
    backgroundColor: '#ffffff',
  },
  ImageUser: {
    alignSelf: 'center',
  },
  ButtonBody:
  {
    flex: 1,
    margin: '5%',
    padding: '5%',
    backgroundColor: 'white',
    borderColor: "black",
    elevation: 6
  }
})
