import { DarkTheme } from '@react-navigation/native';
import React, { useState } from 'react'
import { ScrollView, StyleSheet, TouchableOpacity, View } from 'react-native';
import { Avatar, FAB, Surface, Text } from 'react-native-paper';
import { SafeAreaProvider } from 'react-native-safe-area-context';


const dataItem = ["Tổng hợp 20 câu hỏi điểm liệt", "Khái niệm và quy tắc", "Văn hóa và đạo đức lái xe", "Kỹ thuật lái xe", "Biển báo đường bộ", "Sa hình"]
const dataDetailItem = ["20 Câu hỏi diểm liệt", "Gồm 83 câu hỏi", "Gồm 5 câu hỏi", "Gồm 12 câu hỏi", "Gồm 65 câu hỏi", "Gồm 35 câu hỏi"]
const Leftcontent = (props) => <Avatar.Icon {...props} size={40} icon="account" />
export default Learning = () => {
    [countEx, SetContEx] = useState(10);

    const Ex = () => {
        return (
            <>
                {dataItem.map((item, index) => (
                    <Surface key={index} style={styles.surfaceUser} theme={DarkTheme}>
                        <Leftcontent style={styles.ImageUser} />
                        <View>
                            <View style={styles.ViewPercent}>
                                <Text style={{ fontSize: 20, fontWeight: 'bold' }}>{item}</Text>
                            </View>
                            <View>
                                <Text style={{ fontSize: 15, left: "5%" }}>{dataDetailItem[index]}</Text>
                            </View>
                        </View>
                    </Surface>
                ))}
            </>
        )
    }

    return (
        <SafeAreaProvider>

            <ScrollView style={styles.container}>
                <View style={styles.viewEx}>
                    <Ex />
                </View>
            </ScrollView>
        </SafeAreaProvider>
    )
}

const styles = StyleSheet.create({
    container:
    {
        flex: 1,
        backgroundColor: "#f5f5f5",
        marginBottom: 80,
    },
    viewEx:
    {
        flex: 1,

    },
    surfaceUser:
    {
        flexDirection: 'row',
        padding: "3%",
        elevation: 6,
        borderRadius: 15,
        backgroundColor: "white",
        margin: "3%"
    },
    ViewPercent:
    {
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        left: "5%"
    },
})
